ST.xhtml:
(* Reseting the position if sprite goes off screen *)
(* If moving left to right *)
IF xCor >= xScreen  AND xSpeed > 0 THEN
	xCor := -SpriteWidth;
(* If moving right to left *)
ELSIF xCor <= -SpriteWidth AND xSpeed < 0 THEN
	xCor := xScreen;	
END_IF
(* If moving from up, down *)
IF yCor >= yScreen AND ySpeed > 0 THEN
	yCor := -SpriteHeight;
(* If moving from down, up *)
ELSIF yCor < -SpriteHeight AND ySpeed < 0 THEN
	yCor := yScreen;	
END_IF

(* Actual moving of the coordinates *)
xCor := xCor + xSpeed;
yCor := yCor + ySpeed;

(*Initialize sprite on first scan*)
IF vInit THEN
	vSpriteDef := init;
	vInit := FALSE;
END_IF

(* Actual moving of the coordinates *)
vSpriteDef.x := vSpriteDef.x + moveX;
vSpriteDef.y := vSpriteDef.y + moveY;

(* Reseting the position if sprite goes off screen *)
(* If moving left to right *)
IF vSpriteDef.x > vSpriteDef.screenWidth THEN
	vSpriteDef.x := -vSpriteDef.width;
(* If moving right to left *)
ELSIF vSpriteDef.x < -vSpriteDef.width THEN
	vSpriteDef.x := vSpriteDef.screenWidth;	
END_IF
(* If moving from up, down *)
IF vSpriteDef.y > vSpriteDef.screenHeight THEN
	vSpriteDef.y := -vSpriteDef.height;
(* If moving from down, up *)
ELSIF vSpriteDef.y < -vSpriteDef.height THEN
	vSpriteDef.y := vSpriteDef.screenHeight;	
END_IF

(*Report x,y for use*)
x := vSpriteDef.x - init.x;
y := vSpriteDef.y - init.y;

TactTimer(IN:=TRUE, PT:=vPeriodTime, ET => vElapsedTime);
IF TactTimer.Q THEN
	TactTimer(IN:=FALSE);
END_IF
(* Direction Up, Down traffic light *)
xgv03.TLight3Red := vElapsedTime <= T#5S OR vElapsedTime >= T#37S AND vElapsedTime <= T#85S;
xgv03.TLight3Yellow := vElapsedTime > T#3S AND vElapsedTime <= T#5S OR vElapsedTime >= T#35S AND vElapsedTime <= T#37S;
xgv03.TLight3Green := vElapsedTime >= T#5S AND vElapsedTime <= T#35S;

(* Mirror the other side of the Up, Down direction *)
xgv03.TLight4Red := xgv03.TLight3Red;
xgv03.TLight4Yellow := xgv03.TLight3Yellow;
xgv03.TLight4Green := xgv03.TLight3Green;

(* Direction Left, Right traffic light *)
